const Header = (props) => {
  return (
    <>
      <h1 style={{ color: props.color }}>{props.name}</h1>
    </>
  )
}

const Statistic = (props) => {
  let { name, data } = props;
  return (
    <>
      {console.log("In ", name, data[name])}
      <p>{name} : {data[name]}</p>
    </>
  )
}

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.name}
  </button>
)

const App = () => {
  // save clicks of each button to its own state
  const [globalData, setGlobalData] = useState({
    good: 0,
    neutral: 0,
    bad: 0
  })

  let tmp=globalData["good"];

  const feedbackClick = (name) => {
    const handler = () => {
      let localData=Object.assign(globalData)

      localData[name]++;
      setGlobalData(localData)
      console.log(name, globalData[name])
    }    
    return handler
  }

  return (
    <div>
      <Header color="purple" name="Unicafe" /> 
      {tmp}
      <hr />
      <h2>Please give your feedback</h2>
      <Button name="good" handleClick={feedbackClick("good")}></Button>
      <Button name="neutral" handleClick={feedbackClick("neutral")}></Button>
      <Button name="bad" handleClick={feedbackClick("bad")}></Button>
      <hr />
      <h2>Statistics</h2>
      <Statistic name="good" data={globalData}></Statistic> {globalData["good"]}
      <Statistic name="neutral" data={globalData}></Statistic>{globalData["neutral"]}
      <Statistic name="bad" data={globalData}></Statistic>{globalData["bad"]} 

    </div>
  )
}




