import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const Header = (props) => {
  return (
    <>
      <h1 style={{color:props.color}}>{props.name}</h1>
    </>
  )
}

const Statistic = (props) => {
  return (
    <>
      <p>{props.name} {props.data[props.data.name]}</p>
    </>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [data, setData] = useState({
    good:0,
    neutral:0,
    bad:0
  });

  return (
    <div>
      <Header color="purple" name="Unicafe"/>
      <h2>Statistics</h2>
      <Statistic name="good" data={data}></Statistic>
      <Statistic name="neutral" data={data}></Statistic>
      <Statistic name="bad" data={data}></Statistic>
    </div>
  )
}

ReactDOM.render(
  <App />, 
  document.getElementById('root')
)



