import React, { useState } from 'react';
import ReactDOM from 'react-dom';


const Header = (props) => {
  return (
    <>
      <h1 style={{ color: props.color }}>{props.name}</h1>
    </>
  )
}

const Statistic = (props) => {
  let { name, data } = props;
  return (
    <>
      <p>{name} : {data[name]}</p>
    </>
  )
}

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.name}
  </button>
)

const App = () => {

  const [data, setData] = useState({
    good: 0,
    neutral: 0,
    bad: 0
  })

  let computedData={
    all: Object.values(data).reduce((prev,elem)=>prev+elem,0),
    average:(data.good-data.bad)/all,
    positive:data.good/all
  }

  const feedbackClick = (name) => {
    const handler = () => {
      let localData = Object.assign({}, data);
      computedData[name]=localData.
      localData[all]=
      setData(localData);
    }
    return handler;
  }

  return (
    <div>
      <Header color="purple" name="Unicafe" />
      <hr />
      <h2>Please give your feedback</h2>
      <Button name="good" handleClick={feedbackClick("good")}></Button>
      <Button name="neutral" handleClick={feedbackClick("neutral")}></Button>
      <Button name="bad" handleClick={feedbackClick("bad")}></Button>
      <hr />
      <h2>Statistics</h2>
      <Statistic name="good" data={data}></Statistic>
      <Statistic name="neutral" data={data}></Statistic>
      <Statistic name="bad" data={data}></Statistic>
      <Statistic name="all" data={computedData}></Statistic>
      <Statistic name="average" data={computedData}></Statistic>
      <Statistic name="positive" data={computedData}></Statistic>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'));
