import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const Header = (props) => {
  return (
    <>
      <h1 style={{color:props.color}}>{props.name}</h1>
    </>
  )
}

const Statistic = (props) => {
  let {name, data}=props;
  return (
    <>
      <p>{name} : {data[name]}</p>
    </>
  )
}

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.text}
  </button>
)

const App = () => {
  // save clicks of each button to its own state
  const [data, setData] = useState({
    good:0,
    neutral:0,
    bad:0
  });

  return (
    <div>
      <Header color="purple" name="Unicafe"/>
      <hr/>
      <h2>Give feedback</h2>
      <Button name="good" handleClick=""></Button>
      <Button name="neutral" handleClick=""></Button>
      <Button name="bad" handleClick=""></Button> 
      <hr/>
      <h2>Statistics</h2>
      <Statistic name="good" data={data}></Statistic>
      <Statistic name="neutral" data={data}></Statistic>
      <Statistic name="bad" data={data}></Statistic>
    </div>
  )
}

ReactDOM.render(
  <App />, 
  document.getElementById('root')
)



